import React from "react";

class EditFishForm extends React.Component {

  handleSubmit = (event) =>{
    console.log(event.target.value,
    );

    const updatedFish = [...this.props.fish]
  }

  render() {
    const { name, price, status, desc, image } = this.props.fish;
    return (
      <div className="fish-edit">
        <input
          ref={this.iname}
          name="name"
          type="text"
          onChange={this.handleSubmit}
          placeholder="Name"
          value={name}
        />
        <input
          ref={this.iprice}
          name="price"
          type="text"
          onChange={this.handleSubmit}
          placeholder="Price"
          value={price}
        />
        <select ref={this.istatus} value={status}>
          <option value="available"> Fresh!</option>
          <option value="unavailable"> sold out!</option>
        </select>
        <textarea
          ref={this.idesc}
          name="desc"
          onChange={this.handleSubmit}
          placeholder="desc"
          value={desc}
        ></textarea>
        <input
          ref={this.iimage}
          name="image"
          type="text"
          onChange={this.handleSubmit}
          placeholder="Image"
          value={image}
        />
      </div>
    );
  }
}

export default EditFishForm;
