import React from 'react';


class EditFishForm extends React.Component {

  render() {
    const {name,price,status,desc,image} = this.props.fish;
    return(
      <div className='fish-edit'>
        <input ref ={this.iname} name="name" type= "text" placeholder="Name" value={name}  />
        <input  ref ={this.iprice} name="price" type= "text" placeholder="Price" value={price}/>
        <select ref={this.istatus} value ={status} >
          <option value = "available"> Fresh!</option>
          <option value = "unavailable"> sold out!</option>
        </select>
        <textarea ref ={this.idesc} name="desc" placeholder="desc" value={desc}></textarea>
        <input ref ={this.iimage} name="image" type= "text" placeholder = "Image" value={image}/>

      </div>
    )
  }

}

export default EditFishForm;
